import { useState } from "react";


function NavBar (){

    const [isHovered, setIsHovered] = useState(false)

    return (
    <>
    <div
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
    >
        
            <button> touch me </button>
            { isHovered && (
        <table border="1">
          <thead>
            <tr>
              <th>Header 1</th>
              <th>Header 2</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>Data 1</td>
              <td>Data 2</td>
            </tr>
          </tbody>
        </table>
      )}
        
    </div>
    </>
    );
}
export default NavBar;
