const [items,setItems] = useState([
        {   
            id:1,
            checked:false,
            item: "item 1"
        },
        {   
            id:2,
            checked:false,
            item: "item 2"
        },
        {   
            id:3,
            checked:false,
            item: "item 3"
        }
    ])
    const [newItem,setNewItem] = useState('')
