import { useState } from "react";

function Buttons (){
    return(
        <div className="Button">
                <button> touch me </button>
                <button> touch me </button>
                <button> touch me </button>
        </div>
    );
}
function NavBar (){

    const [isHovered, setIsHovered] = useState(false)

    return (
    <>
    <div
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
    >
        
            <button> touch me </button>
            { isHovered && (<Buttons/>)}
        
    </div>
    </>
    );
}
export default NavBar;
