const [items,setItems] = useState([
        {   
            id:1,
            checked:false,
            item: "item 1"
        },
        {   
            id:2,
            checked:false,
            item: "item 2"
        },
        {   
            id:3,
            checked:false,
            item: "item 3"
        }
    ])
    const handleCheck= (id) => {
        const listItems = items.map((item) => item.id === id ? {...item,checked:!item.checked} :item)
        setItems(listItems)
        localStorage.setItem('shoppinglist',JSON.stringify(listItems))
    }
    const handleDelete =(id) =>{
        const listItems =items.filter((item) =>item.id !==id);
        setItems(listItems)
        localStorage.setItem('shoppinglist',JSON.stringify(listItems))  
    }
    const handleAdd = () => {
        let listItems = items
        listItems.push({id : items.length+1,item : `item `+items.length,checked:false})

        setItems(listItems)
        localStorage.setItem('shoppinglist',JSON.stringify(listItems))
    }
  return (
    <div className="App">
      <Header title="groceries"/>
      <Content 
        items



      />
      <Footer/>
    </div>
  );
}

export default App;
