import { useState } from "react";

function Buttons (){
    const buttons = ["Button 1", "Button 2", "Button 3"];
    const buttons1 = ["Button 1", "Button 2"];
    const buttons2 = ["Button 1", "Button 2", "Button 3", "Button 4", "Button 5"];
    const buttons3 = ["Button 1", "Button 2", "Button 3", "Button 4"];
    const buttons4 = ["Button 1"];
    return (
        <div className="buttons-row">
        <div className="button-column">
            {buttons.map((label, index) => (
                <button key={index} className="column-button">{label}</button>
            ))}
        </div>
        <div className="button-column-1">
            {buttons1.map((label, index) => (
                <button key={index} className="column-button">{label}</button>
            ))}
        </div>
        <div className="button-column-2">
            {buttons.map((label, index) => (
                <button key={index} className="column-button">{label}</button>
            ))}
        </div><div className="button-column-3">
            {buttons.map((label, index) => (
                <button key={index} className="column-button">{label}</button>
            ))}
        </div>
        </div>
    );
}
function NavBar (){

    const [isHovered, setIsHovered] = useState(false)

    return (
    <>
    <div
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
    >
        
            <button className="column-button"> button 0</button>
            { isHovered && (<Buttons/>)}
        
    </div>
    </>
    );
}
export default NavBar;
